(function() {
  var init = function(obj) {
    var li_children =
      "<div id='childBar' style='width:100%;height:20px;border:1px solid;border-radius:3px'>" +
      "<div id='childBarColor' style='width:0;border:0;background-color:rgba(250,59,127,0.8);height:18px;'>" +
      "</div>" +
      "</div>";
    var li = document.createElement("li");
    $(li).addClass("fragment");
    $(li).html(li_children);
    obj.node.append(li);
  };
  widget.register("li", {
    init: init
  });
})();

(function() {
  var init = function(obj) {
    var data = obj.data;
    var name = data.name;
    var size = data.size;
    var parent =
      "<td>" +
      name +
      "<div class='wraper'><a class='linkWrapper'></a></div>" +
      "</td>" +
      "<td>" +
      size +
      "</td>" +
      "<td><div style='overflow:hidden'><div id='totalBar' style='float:left;width:70%;height:30px;border:1px solid;border-radius:3px'>" +
      "<div id='totalBarColor' style='width:0;border:0;background-color:rgba(232,152,39,0.8);height:28px;'></div>" +
      "<p class='speed'></p>" +
      "</div>" +
      "<div class='control-container'>" +
      '<input class="btn btn-default control-upload" type="button" style="width:auto;vertical-align:middle;text-align: center;height:auto;line-height:18px;" value="开始上传">' +
      "</div></div>" +
      // "<div><input class='btn btn-default resume' type='button' value='查看分块进度'></div>" +
      // "<ul class='fragment-group hide'>" +
      // "</ul>"+
      "</td>";
    var tr = document.createElement("tr");
    $(tr).html(parent);
    console.log(obj.node)
    obj.node[0].lastChild.remove()//删除上一个节点
    obj.node.append(tr);
    for (var i = 0; i < data.num; i++) {
      widget.add("li", {
        data: "",
        node: $(tr).find(".fragment-group")
      });
    }
    $(tr)
      .find(".resume")
      .on("click", function() {
        var ulDom = $(tr).find(".fragment-group");
        if (ulDom.hasClass("hide")) {
          ulDom.removeClass("hide");
        } else {
          ulDom.addClass("hide");
        }
      });
    return tr;
  };
  widget.register("tr", {
    init: init
  });
})();
